<?php
class Solution
{
    public function findTheDifference($s, $t)
    {
        $sa = str_split($s);
        $ta = str_split($t);

        $length = count($sa);
        for ($i = 0; $i < $length; $i++) {
            if ($sa[$i] != $ta[$i]) {
                return $ta[$i];
            }
        }
        return $ta[$i];
    }

}

$solution = new Solution();

